mapper.select:
m where (select case when sum(point)<500 then '플레인' when sum(point)>=500 and sum(point)<10000 then '브론즈' when sum(point)>=10000 and sum(point)<100000 then '실버' when sum(point)>=100000 and sum(point)<1000000 then '골드' else '다이아몬드' end from points where grantee=m.member_id) in
#{it}
order by member_id
